	public function convert2Value($str) {
		$str = strtolower($str);
		$x = array(" ", "+", "-");
		$y = array("", "plus", "minus");
		return str_replace($x, $y, $str);
	}
	public static function getCaptionTransitionsCode() {
		$return = '';
		foreach ( self::$_transitions as $trans ) {
			$return .= '_CaptionTransitions["'.$trans['name'].'"] = '.$trans['code'].';';
		}
		return $return;
	}
	public static function toOptionArray() {
//		$options[] = array (
//			'value' => '',
//			'label' => Mage::helper('slideshow')->__ ('--Please Select--')
//		);
		foreach ( self::$_transitions as $trans ) {
			$options[] = array(
				'value' => self::convert2Value($trans['name']),
				'label' => $trans['name']
			);
		}
		return $options;
	}
