	public function convert2Value($str) {
		$str = strtolower($str);
		$x = array(" ", "+", "-");
		$y = array("", "plus", "minus");
		return str_replace($x, $y, $str);
	}
	public static function getTransitionsCode($values) {
		$return = '';
		
		$values_arr = explode(',', $values);
		foreach ( $values_arr as $value ) {
			foreach ( self::$_transitions as $trans ) {
				if( self::convert2Value($trans['name']) == $value )
					$return .= $trans['code'] . ',';
			}
		}
		
		return preg_replace('/,$/', '', $return);
	}
	public static function toOptionArray() {
//		$options[] = array (
//			'value' => '',
//			'label' => Mage::helper('slideshow')->__ ('--Please Select--')
//		);
		foreach ( self::$_transitions as $trans ) {
			$options[] = array(
				'value' => self::convert2Value($trans['name']),
				'label' => $trans['name']
			);
		}
		return $options;
	}
